    private void jCheckBoxActionPerformed(java.awt.event.ActionEvent evt) {  
    	for(JCheckBox j : firstRow){
    		if(evt.getSource().equals(j)){ //clicked one in firstRow
    		for(JCheckBox q : firstRow){
    			if(! q.equals(j)) q.setSelected(false);
    		}
    	}
    	}
    	for(JCheckBox jcheck : secondRow){
    		if(evt.getSource().equals(jcheck)){ //clicked one in second row
    		for(JCheckBox q : secondRow){
    			if(! q.equals(jcheck)) q.setSelected(false);
    		}
    	}
    	}
    }          
    
    private boolean isFinished(){
    	return isFinished;
    }
    
    private void jButton1ActionPerformed(java.awt.event.ActionEvent e){
    	if(isChecked(firstRow) && isChecked(secondRow)) isFinished = true;
    	this.dispose();
    	//System.out.println(isFinished());
    }
    
    private boolean isChecked(JCheckBox[] j){
    	boolean b = false;
    	for(JCheckBox a : j){
    		if(a.isSelected()) b = true;
    	}
    	return b;
    }
    
    public int getShipType(){
    	for(int i = 0; i < firstRow.length; i++){
    		if(firstRow[i].isSelected()) return i;
    	}
    	return -1;
    }
    
    public int getLaserColor(){
    	for(int i = 0; i < secondRow.length; i++){
    		if(secondRow[i].isSelected()) return i;
    	}
    	return -1;
    }

